                  accordionItemBuilder({
                    title: 'Collection',
                    children: (
                      <Box direction="vertical" gap="12px">
                        {collections.map(collection => (
                          <Checkbox
                            checked={tagExists({
                              type: 'collection',
                              value: collection,
                            })}
                            onChange={() =>
                              toggleTag({
                                type: 'collection',
                                value: collection,
                              })
                            }
                          >
                            {collection}
                          </Checkbox>
                        ))}
                      </Box>
                    ),
                  }),
                  accordionItemBuilder({
                    title: 'Inventory',
                    children: (
                      <RadioGroup
                        value={getInventoryTagValue()}
                        onChange={handleInventoryTag}
                      >
                        <RadioGroup.Radio value="All Statuses">
                          All Statuses
                        </RadioGroup.Radio>
                        <RadioGroup.Radio value="In Stock">
                          In Stock
                        </RadioGroup.Radio>
                        <RadioGroup.Radio value="Out of Stock">
                          Out of Stock
                        </RadioGroup.Radio>
                      </RadioGroup>
                    ),
                  }),
                  accordionItemBuilder({
                    title: 'Price',
                    children: (
                      <Slider
                        marks={{
                          0: `$${price[0]}`,
                          1: `$${price[1]}`,
                          2: `$${price[2]}`,
                          3: `$${price[3]}`,
                        }}
                        min={0}
                        max={3}
                        value={[0, 3]}
                        onChange={handlePriceTag}
                        value={getPriceTagValue()}
                      />
                    ),
                  }),
                ]}
              />
              <SidePanel.Footer>
                <Box gap="12px" paddingBottom="SP4">
                  <Text size="small">3 filters applied</Text>
                  <TextButton size="small">Clear</TextButton>
                </Box>
                <Button priority="secondary" onClick={closePanel} fullWidth>
                  Close
                </Button>
              </SidePanel.Footer>
            </SidePanel>
          </div>
        </Box>
      </Page.Content>
    </Page>
  );
};

