            id: 2,
            title: `Item`,
          },
        ];

        const filtered = items.filter(({ title }) =>
          title.toLowerCase().includes(searchQuery.toLowerCase()),
        );

        resolve({
          items: filtered.slice(offset, offset + limit),
          totalCount: filtered.length,
        });
      }, 2000),
    );
  return (
    <SelectorList
      dataSource={data}
      multiple
      renderNoResults={() => (
        <Box height="280px" align="center" verticalAlign="middle">
          <EmptyState
            image="EmptyState_Generic2.svg"
            title="No results found"
            subtitle="No items match your search criteria. Try to search by another keyword."
          >
            <TextButton>Clear Search</TextButton>
          </EmptyState>
        </Box>
      )}
    >
      {({ renderList }) => (
        <CustomModalLayout
          title="Import items"
          primaryButtonText="Import"
          secondaryButtonText="Cancel"
          onCloseButtonClick={() => {}}
          removeContentPadding
          showFooterDivider
          showHeaderDivider
        >
          {renderList()}
        </CustomModalLayout>
      )}
    </SelectorList>
  );
};

