() => {
  const [showMove, setShowMove] = React.useState(true)

  React.useEffect(() => {
    setTimeout(() => setShowMove((previous) => !previous), 1500)
  }, [showMove])

  const renderContent = () => (
    <StorybookComponents.Placeholder width="240px" height="120px">
      Element with animated transition
    </StorybookComponents.Placeholder>
  )

  return (
