() => {
  const [child, setChild] = React.useState([]);

  const handleRemove = id => {
    setChild(previous => previous.filter(c => c.id !== id));
  };

  const handleAddChild = () => {
    const id = new Date().getTime();
    const item = {
      id,
      node: (
        <TableListItem
          key={id}
          showDivider
          onClick={() => {
            handleRemove(id);
          }}
          options={[
            { value: <Text>Order {child.length}</Text>, align: 'left' },
            { value: <Text>2 minutes ago</Text>, align: 'center' },
            {
              value: (
                <TableActionCell
                  size="small"
                  primaryAction={{
                    text: 'Mark as fullfilled',
                    skin: 'inverted',
                    onClick: () => handleRemove(id),
                  }}
                />
              ),
            },
          ]}
        />
      ),
    };
    setChild([item, ...child]);
  };

  return (
    <Card hideOverflow>
      <Card.Header
        title="Your order waitlist"
        suffix={
          <Button
            size="small"
            prefixIcon={<Icons.AddSmall />}
            onClick={() => handleAddChild()}
          >
            Add new order
          </Button>
        }
      />
      <Card.Divider />
      <Box direction="vertical">
        <Transition
          enterAnimation={{
            fadeIn: {
              delay: 'long',
              duration: 'slow02',
              easing: 'enterEasing',
            },
            moveIn: {
              direction: 'bottomToTop',
              distance: '30px',
              duration: 'slow02',
              delay: 'long',
              easing: 'enterEasing',
            },
          }}
          exitAnimation={{
            fadeOut: {
              delay: 'long',
              duration: 'slow02',
              easing: 'exitEasing',
            },
            moveOut: {
              direction: 'leftToRight',
              distance: '30px',
              duration: 'slow02',
              delay: 'long',
              easing: 'exitEasing',
            },
          }}
        >
          {child.map(c => c.node)}
        </Transition>
      </Box>
    </Card>
  );
};

